  props: {
    value: {
      type: [String, Number],
      required: true
    }
  },
  data () {
    return {
      panes: []
    }
  },
  components: {
    TabContent
  },
  render () {
    return (
      <div class="tabs">
        <ul class="tabs-panel">
          {this.$slots.default}
        </ul>
        <tab-content panes={this.panes} />
      </div>
    )
  },
  methods: {
    onChange (index) {
      this.$emit('change', index)
    }
  }
}
</script>
