  <div class="helper">
    <span class="left">{{unCompletedTodoNumber}} items left</span>
    <!-- <span class="tabs">
      <span
        v-for="state in states"
        :key="state"
        :class="['state', filter === state ? 'actived' : '']"
        @click="toggleFilter(state)"
      >
        {{state}}
      </span>
    </span> -->
    <span class="clear" @click="clearAllCompleted">clear completed</span>
