<script>
export default {
  props: {
    todos: {
      type: Array,
      required: true
    }
    // filter: {
    //   type: String,
    //   required: true
    // }
  },
  // data () {
  //   return {
  //     states: ['all', 'active', 'completed']
  //   }
  // },
  computed: {
    unCompletedTodoNumber () {
      return this.todos.filter(todo => !todo.completed).length
    }
  },
  methods: {
    // toggleFilter (state) {
    //   this.$emit('filterList', state)
    // },
    clearAllCompleted () {
      if (this.todos.length === this.unCompletedTodoNumber) {
        this.$notify({
          content: '暂时没有已完成的事务'
        })
        return
      }
      this.$emit('clearAllCompleted')
    }
  }
}
</script>
<style lang="stylus" scoped>
  .helper {
    padding 10px 15px
  }
  .state {
    margin-left 20px
    cursor pointer
    text-align center
    display inline-block
    padding 0 3px
    border 1px solid transparent
  }
  .actived {
    border-color #d5d5d5
    border-radius 3px
    cursor default
  }
  .clear {
    float right
    cursor pointer
    &:hover {
      text-decoration underline
    }
