    ...mapActions([
      'fetchTodos',
      'addTodo',
      'updateTodo',
      'deleteTodo',
      'deleteAllCompleted'
    ]),
    open (id) {
      this.list.filter(item => {
        if (item.id === id) {
          item.fold = !item.fold
        }
      })
    },
    tabChange (value) {
      this.filter = value
    },
    handleAdd (e) {
      let val = e.target.value.trim()
      if (!val) {
        this.$notify({
          content: '请输入事务内容'
        })
        return
      }
      let todo = {
        content: val,
        completed: false
      }
      this.addTodo(todo)
      e.target.value = ''
    },
